listeners.beforehide:
function() {
                                    if(this.toolComponents) {
                                        var tool = this.toolComponents.smartimageCrop.toolRef.userInterface;
                                        if(tool.isVisible()) { tool.hide(); }
                                    }
                                }

listeners.beforeshow:
function() {
                                    if(this.toolComponents) {
                                        var tool = this.toolComponents.smartimageCrop.toolRef.userInterface;
                                        if(!tool.isVisible()) {
                                            tool.show();
                                            tool.setPosition(tool.saveX, tool.saveY);
                                        }
                                    }
                                }

